<?php

class WeeverAppTab {

    const MOVE_UP = 'up';
    const MOVE_DOWN = 'down';

    protected $_data = array();
    protected $_changed = array();
    private $_subtabs = array();

    private $_default_icon_images = array(
                                        'social' => "",
                                        'contact' => "",
                                        'page' => "",
                                        'photo' => "",
                                        'blog' => "",
                                        'calendar' => "",
                                        'video' => "",

                                    );

    public function __construct($id, $component, $name, $published, $ordering, $icon = false, $icon_image = false) {
        $this->_data['id'] = $id;
        $this->_data['component'] = $component;
        $this->_data['name'] = $name;
        $this->_data['icon'] = ($icon ? $icon : '');
        $this->_data['published'] = $published;
        $this->_data['ordering'] = $ordering;

        if ( ! $icon_image ) {
            // Load the default if found
            if ( isset( $this->_default_icon_images[$component] ) )
                $this->_data['icon_image'] = $this->_default_icon_images[$component];
            else
                $this->_data['icon_image'] = '';
        } else {
            $this->_data['icon_image'] = $icon_image;
        }
    }

    public function & __get($var) {
        switch ( $var ) {
            default:
                if ( array_key_exists( $var, $this->_data ) )
                    return $this->_data[$var];
                else
                    throw new Exception( __( 'Invalid parameter name' ) );
        }
    }

    public function __set($var, $val) {
        switch ( $var ) {
            case 'id':
                throw new Exception( __( 'Cannot edit the tab id after loading' ) );
                break;
            default:
                if ( array_key_exists( $var, $this->_data ) ) {
                    $this->_data[$var] = $val;
                    $this->_changed[$var] = $var;
                } else {
                    throw new Exception( __( 'Invalid parameter name' ) );
                }
        }
    }

    /**
     * Add an already created subtab to this tab's elements
     *
     * @param WeeverAppSubtab $subtab
     */
    public function add_subtab(&$subtab) {
        $this->_subtabs[] = $subtab;
    }

    /**
     * Creates a new subtab from the given parameters and pushes to the Weever Apps server
     *
     * @param array tab data
     */
    public function create_subtab($data) {

        $hash = isset( $data['name'] ) ? md5( microtime() . $data['name'] ) : ( md5( microtime() ) );

        // Build the contact details if applicable
        if ( $this->component == 'contact' ) {
            $contact = new stdClass;
			$contact->telephone = $data['phone'];
			$contact->email_to = $data['email'];
			$contact->address = $data['address'];
			$contact->town = $data['town'];
			$contact->state = $data['state'];
			$contact->country = $data['country'];
			$contact->googlemaps = ( $data['googlemaps'] ? 1 : 0 );
			$contact->emailform = ( $data['emailform'] ? 1 : 0 );
			$data['var'] = json_encode( $contact );
        }

        $postdata = array(
				'name' => isset( $data['name'] ) ? $data['name'] : null,
				'hash' => $hash,
				'component' => isset( $data['component'] ) ? $data['component'] : null,
				'published' => isset( $data['published'] ) ? $data['published'] : null,
				'component_id' => isset( $data['component_id'] ) ? $data['component_id'] : null,
				'ordering' => isset( $data['ordering'] ) ? $data['ordering'] : null,
				'id' => isset( $data['id'] ) ? $data['id'] : null,
				'rss_update' => isset( $data['rss_update'] ) ? $data['rss_update'] : null,
				'icon' => isset( $data['icon'] ) ? $data['icon'] : null,
				'rss' => isset( $data['rss'] ) ? $data['rss'] : null,
				'type' => isset( $data['type'] ) ? $data['type'] : null,
				'component_behaviour' => isset( $data['component_behaviour'] ) ? $data['component_behaviour'] : null,
				'var' => isset( $data['var'] ) ? $data['var'] : null,
				'parent_tab_id' => isset( $data['parent_tab_id'] ) ? $data['parent_tab_id'] : null,
				'cms_feed' => isset( $data['cms_feed'] ) ? $data['cms_feed'] : null,
				'app' => 'ajax',
				'm' => "add_tab",
				);

        $result = WeeverHelper::send_to_weever_server($postdata);

        if ( 'Item Added' != $result )
            throw new Exception( sprintf( __( 'Error adding new tab: %s' ), $result ) );

        // TODO: Remove this shortly as it is no longer be necessary
		$postdata = array(
				'local_tab_id' => rand(1000,5000),
				'hash' => $hash,
				'app' => 'ajax',
				'm' => 'tab_local_id'
				);

        $result = WeeverHelper::send_to_weever_server($postdata);
    }

    public function & get_subtabs() {
        return $this->_subtabs;
    }

    public function & get_subtab($id) {
        foreach ( $this->_subtabs as $subtab ) {
            if ( $subtab->id == $id )
                return $subtab;
        }
    }

    /**
     * Function to show if this is a top level (toolbar) tab or not
     *
     * @return bool
     */
    public function is_top_level_tab() {
        return true;
    }

    public function delete() {
		$postdata = array(
				'cloud_tab_id' => $this->id,
				'app' => 'ajax',
				'm' => 'delete_tab',
				);

        $result = WeeverHelper::send_to_weever_server($postdata);

        if ( 'Item Deleted' != $result )
            throw new Exception( __( 'Error deleting tab' ) );
    }

