    public function save() {

        // Name change
        if ( isset( $this->_changed['name'] ) ) {
			$postdata = array(
				'name' => $this->name,
				'id' => $this->id,
				'app' => 'ajax',
				'm' => "edit_tab_name",
				);

		    $result = WeeverHelper::send_to_weever_server($postdata);

		    if ( 'Tab Changes Saved' != $result )
		        throw new Exception( __( 'Error saving tab name' ) );
        }

        if ( isset( $this->_changed['icon_image'] ) ) {
	        $postdata = array(
				'icon' => $this->icon_image,
				'type' => $this->component,
				'app' => 'ajax',
				'm' => "edit_tab_icon",
				);

			$result = WeeverHelper::send_to_weever_server($postdata);

			if ( 'Icon Saved' != $result )
		        throw new Exception( __( 'Error saving tab icon' ) );
        }

        if ( isset( $this->_changed['published'] ) ) {
			$postdata = array(
				'published' => $this->published,
				'app' =>'ajax',
				'm' => 'publish_tab',
				'cloud_tab_id' => $this->id
				);

            $result = WeeverHelper::send_to_weever_server($postdata);

            if ( 'Item Published' != $result && 'Item Unpublished' != $result )
                throw new Exception( __( 'Error updating published status' ) );
        }

        // TODO: Handle remaining changes

        $this->_changed = array();
    }
}